-- (b)ポイント残高連携リスト → mTG会員_ポイント有効期限_temp
-- 
-- 2025/05/16  新規作成
--------------------------------------------------------------------------
-- 変数宣言
DECLARE @today_jst datetime ;
DECLARE @today varchar(8);
SET @today_jst = CONVERT(DATE, GETDATE() AT TIME ZONE 'UTC' AT TIME ZONE 'Tokyo Standard Time');
SET @today = FORMAT(@today_jst, 'yyyyMMdd');

-- mTG会員_ポイント有効期限_temp
TRUNCATE TABLE [omni].[omni_ods_mytginfo_trn_mtgmaster_point_expiration_date_temp];

INSERT INTO [omni].[omni_ods_mytginfo_trn_mtgmaster_point_expiration_date_temp] --mTG会員_ポイント有効期限_tempに登録
SELECT MTGID                                    -- mTG会員ID
      ,MIN(EXPIRE_DATE) AS EXPIRE_DATE          -- 直近の有効期限
      ,SUM(BALANCE_POINT) AS BALANCE_POINT_ALL  -- 残ポイントを集計
FROM [omni].[omni_ods_mytgpnt_trn_pnt_bal]      -- ポイント残高連携リスト
WHERE EXPIRE_DATE >= @today                     -- 有効期限>=実行日
GROUP BY MTGID                                  -- 会員ID
;
